<script>
export default {
  data() {
    return {
      // helper variables for showing the addons
      breakfastSet: false,
      snacksSet: false,
      veganBowlSet: false,
      snackNames: 'empty',
      breakfastNames: 'empty',
    }
  },
  created() {
    let self = this
    // event for checking the addons
    this.$nuxt.$on('check-addons', function () {
      self.checkTheAddons()
    })
  },
  mounted() {
    this.checkTheAddons()
  },
  methods: {
    addBreakfast() {
      $nuxt.$emit('toggle-view', 3)
      $nuxt.$emit('open-view', 3)
      $nuxt.$emit('last-meal-selection-exit')

      if (window.innerWidth <= 1282) {
        // emit hide navbar event
        $nuxt.$emit('nav-bar-toggle', false)
        $nuxt.$emit('order-panel-mobile', false)
      }
    },
    addSnack() {
      $nuxt.$emit('toggle-view', 5)
      $nuxt.$emit('open-view', 5)
      $nuxt.$emit('last-meal-selection-exit')
      if (window.innerWidth <= 1282) {
        // emit hide navbar event
        $nuxt.$emit('nav-bar-toggle', false)
        $nuxt.$emit('order-panel-mobile', false)
      }
    },
    addVeganBowl() {
      $nuxt.$emit('toggle-view', 6)
      $nuxt.$emit('open-view', 6)
      $nuxt.$emit('last-meal-selection-exit')
      if (window.innerWidth <= 1282) {
        // emit hide navbar event
        $nuxt.$emit('nav-bar-toggle', false)
        $nuxt.$emit('order-panel-mobile', false)
      }
    },
    checkTheAddons() {
      if (this.$store.state.cart.selectedBreakfast.length > 0) {
        this.breakfastSet = true
        this.breakfastNames = ''

        for (
          let i = 0;
          i < this.$store.state.cart.selectedBreakfast.length;
          i++
        ) {
          if (
            typeof this.$store.state.cart.selectedBreakfast[i] !== 'undefined'
          ) {
            if (i != 0) {
              this.breakfastNames +=
                ' & ' + this.$store.state.cart.selectedBreakfast[i].name
            } else {
              this.breakfastNames = this.$store.state.cart.selectedBreakfast[
                i
              ].name
            }
          }
        }
      } else {
        this.breakfastSet = false
      }

      if (this.$store.state.cart.selectedSnacks.length > 0) {
        this.snacksSet = true
        this.snackNames = ''
        for (let i = 0; i < this.$store.state.cart.selectedSnacks.length; i++) {
          if (typeof this.$store.state.cart.selectedSnacks[i] !== 'undefined') {
            if (i != 0) {
              this.snackNames +=
                ' & ' + this.$store.state.cart.selectedSnacks[i].name
            } else {
              this.snackNames = this.$store.state.cart.selectedSnacks[i].name
            }
          }
        }
      } else {
        this.snacksSet = false
      }

      if (this.$store.state.cart.selectedVeganBowl.length > 0)
        this.veganBowlSet = true
      else this.veganBowlSet = false
    },
  },
}
</script>

