<script>
import mealPortionAddition from './mealPortionAddition.vue'
import {
  getAllMealsByIndex,
  getWholeMealProtein,
  getWholeMealCalories,
  getWholeMealCarbs,
  getWholeMealFat,
  getVeganBowlInOneItem,
  getWholeSnackValues,
  getWholeBreakfastValues,
} from './../mealFunctions.js'

import { isReadyToBePlacedOrder } from './../cartFunctions.js'
import promoCode from './../wizard/components/promoCode.vue'

import mealAddons from './mealAddons.vue'
import pointsToReceived from './components/pointsToBeReceived.vue'

export default {
  components: {
    getAllMealsByIndex,
    mealPortionAddition,
    mealAddons,
    pointsToReceived,
    promoCode,
  },
  data() {
    return {
      sideDishes: [],
      mealIndex: 0,
      showReviewButton: false,
      isCheckState: false,
      showPoints: false,
      priceForPoints: 0,
      btnText: 'Next meal',
      primaryBtnAllowed: true,
      title: 'your order',
      showMealInfo: true,
      veganPanel: false,
      veganBowl: null,
      thisMealValue: true,
      snackPanel: false,
      snackValues: null,
      breakfastPanel: false,
      breakfastValues: null,
      showPromoCode: false,
      cartHidden: true,
      addonNumber: false,
    }
  },
  watch: {
    mealIndex() {
      this.sideDishes = getAllMealsByIndex(this.$store, this.mealIndex)
    },
  },
  created() {
    // helper variable
    const self = this

    // event when show the panel
    this.$nuxt.$on('show-addon-order-panel', (useCase) => {
      switch (useCase) {
        case 'breakfast':
          break
      }
      this.title = 'value'
    })

    // event when the meals index view is changed
    this.$nuxt.$on('meal-updated', (index) => {
      this.mealIndex = index
    })
    // when the user selects a new side dish for a meal
    this.$nuxt.$on('side-dish-updated', () => {
      this.sideDishes = getAllMealsByIndex(this.$store, this.mealIndex)
    })
    // when the user is in the last meal selection, he can select the review button
    this.$nuxt.$on('last-meal-selection-enter', () => {
      self.showReviewButton = true
      self.isCheckState = true
    })
    // when the user is not in the last meal selection
    this.$nuxt.$on('last-meal-selection-exit', () => {
      self.showReviewButton = false
      self.isCheckState = false
    })
    // when is the need to show how much points the user will get
    this.$nuxt.$on('show-and-count-points', (show, value) => {
      this.showPoints = show
      this.priceForPoints = value
    })

    // when the entry meal is selected then, show the button as active
    this.$nuxt.$on('entry-meal-selected', (state) => {
      this.primaryBtnAllowed = !state
    })

    // when the view is beign entered
    this.$nuxt.$on('cart-view-entered', () => {
      this.btnText = 'To payment'
      this.primaryBtnAllowed = !isReadyToBePlacedOrder(this.$store)
      this.thisMealValue = false
      this.showPromoCode = true
      this.cartHidden = false
    })

    // when the view is beign entered
    this.$nuxt.$on('cart-view-exited', () => {
      this.btnText = 'Next meal'
      this.thisMealValue = true
      this.showPromoCode = false
      this.cartHidden = true
    })

    this.$nuxt.$on('order-panel-mobile', (valueBool) => {
      // add the class to this elem
      if (valueBool)
        document
          .getElementById('order-panel-hybrid')
          .classList.add('order-mobile-show')
      else
        document
          .getElementById('order-panel-hybrid')
          .classList.remove('order-mobile-show')
    })

    this.$nuxt.$on('vegan-bowl-values-refresh', () => {
      self.veganBowl = getVeganBowlInOneItem(this.$store)
    })

    this.$nuxt.$on('breakfast-values-refresh', () => {
      self.breakfastValues = getWholeBreakfastValues(this.$store)
    })

    this.$nuxt.$on('snack-values-refresh', () => {
      self.snackValues = getWholeSnackValues(this.$store)
    })

    this.$nuxt.$on('open-view', (view) => {
      switch (view) {
        case 6:
          self.title = 'value'
          self.isCheckState = false
          self.showMealInfo = false
          self.veganBowl = getVeganBowlInOneItem(this.$store)
          self.veganPanel = true
          self.addonNumber = 6
          break

        case 5:
          self.title = 'value'
          self.isCheckState = false
          self.showMealInfo = false
          self.snackValues = getWholeSnackValues(this.$store)
          self.snackPanel = true
          self.addonNumber = 5
          break

        case 3:
          self.title = 'value'
          self.isCheckState = false
          self.showMealInfo = false
          self.breakfastValues = getWholeBreakfastValues(this.$store)
          self.breakfastPanel = true
          self.addonNumber = 3
          break
      }
    })

    this.$nuxt.$on('leave-view', (view) => {
      switch (view) {
        case 6:
          self.veganPanel = false
          self.title = 'your meal plan'
          self.addonNumber = false
          break

        case 5:
          self.snackPanel = false
          self.title = 'your meal plan'
          self.addonNumber = false
          break

        case 3:
          self.breakfastPanel = false
          self.title = 'your meal plan'
          self.addonNumber = false
          break
      }
    })

    // set btn text
    this.$nuxt.$on('change-button-title', (btnText) => {
      self.btnText = btnText
    })
  },
  mounted() {
    this.sideDishes = getAllMealsByIndex(this.$store, this.mealIndex)
  },
  methods: {
    refreshVeganBowl() {
      this.veganBowl = getVeganBowlInOneItem(this.$store)
    },
    toggleReviewOrder() {
      $nuxt.$emit('toggle-view', 2)
      if (window.innerWidth <= 1282) {
        // emit hide navbar event
        $nuxt.$emit('nav-bar-toggle', false)
        $nuxt.$emit('order-panel-mobile', false)
      }
    },
    toggleReviewOrderButton() {
      if (this.showReviewButton) this.showReviewButton = false
      else this.showReviewButton = true
    },
    primaryButtonClick() {
      if (this.btnText === 'Next meal') {
        // emit the event to the next meal
        $nuxt.$emit('next-meal')
      } else if (this.btnText === 'To payment') {
        $nuxt.$emit('to-payment')
      } else if (this.btnText === 'Check out') {
        // go to the cart
        $nuxt.$emit('toggle-view', 4)
        // turn on the navigation
        $nuxt.$emit('nav-bar-toggle', true)
        // hide the order panel
        $nuxt.$emit('order-panel-mobile', false)
      }
    },
    getWholeMealProteinFnc(store, mealIndex) {
      return getWholeMealProtein(store, mealIndex)
    },
    getWholeMealCalories(store, mealIndex) {
      return getWholeMealCalories(store, mealIndex)
    },
    getWholeMealCarbs(store, mealIndex) {
      return getWholeMealCarbs(store, mealIndex)
    },
    getWholeMealFat(store, mealIndex) {
      return getWholeMealFat(store, mealIndex)
    },
    goBack() {
      $nuxt.$emit('order-panel-mobile', false)
      $nuxt.$emit('nav-bar-toggle', true)
      // refresh meal view selection
      $nuxt.$emit('meals-end-set')
    },
    goBackFromAddonView() {
      $nuxt.$emit('toggle-view', this.addonNumber)
      $nuxt.$emit('last-meal-selection-enter')
      $nuxt.$emit('nav-bar-toggle', true)
      $nuxt.$emit('leave-view', this.addonNumber)
    },
  },
}
</script>

