            <div class="summary-content">
              <div
                v-if="this.$store.state.wizard.amountOfMeals > 0"
                class="summary-row"
              >
                <div>
                  <p class="sum-bold">
                    Custom meals plan
                  </p>
                  <p class="sum-semi">
                    {{ this.$store.state.wizard.amountOfMeals }} meals
                  </p>
                </div>
                <div>
                  <p class="sum-bold">{{ getTotalMealPrice() }}$</p>
                </div>
              </div>

              <div
                v-if="this.$store.state.cart.selectedBreakfast.length > 0"
                style="margin-top: 24px;"
                class="summary-row"
              >
                <div>
                  <p class="sum-bold">
                    Breakfast
                  </p>
                  <p class="sum-semi">
                    {{ breakfastNames }}
                  </p>
                </div>
                <div>
                  <p class="sum-bold">{{ getTotalBreakfastPrice() }}$</p>
                </div>
              </div>

              <div
                v-if="this.$store.state.cart.selectedSnacks.length > 0"
                style="margin-top: 24px;"
                class="summary-row"
              >
                <div>
                  <p class="sum-bold">
                    Snacks
                  </p>
                  <p class="sum-semi">
                    {{ snackNames }}
                  </p>
                </div>
                <div>
                  <p class="sum-bold">{{ getTotalSnackPrice() }}$</p>
                </div>
              </div>

              <div
                v-if="this.$store.state.cart.selectedVeganBowl.length > 0"
                style="margin-top: 24px;"
                class="summary-row"
              >
                <div>
                  <p class="sum-bold">
                    Veganbowl
                  </p>
                  <p class="sum-semi"></p>
                </div>
                <div>
                  <p class="sum-bold">{{ getVeganBowlPriceTotal() }}$</p>
                </div>
              </div>

              <div class="summary-horizontal-spacer"></div>
              <div
                v-if="deliveryMode == 1 && deliveryZip && deliveryCost != 0"
                class="summary-row"
              >
                <div><p class="sum-bold">Delivery</p></div>
                <div>
                  <p class="sum-bold"><span v-html="deliveryCost"></span>$</p>
                </div>
              </div>
              <div v-if="couponShow == false" class="summary-row">
                <div>
                  <p class="coupon-link" @click="couponShow = true">
                    Have a coupon?
                  </p>
                </div>
                <div></div>
              </div>
              <div v-if="couponShow">
                <div class="new-input-wrapper ext-input-wrp coupon-input">
                  <input
                    v-model="couponCode"
                    placeholder="Coupon code"
                    type="text"
                  />
                  <p v-show="couponCode != ''" class="coupon-apply-a">APPLY</p>
                </div>
              </div>
            </div>
            <p class="summary-total-price">
              Total: <span class="s-price-bold">${{ getTotalPrice() }}</span>
            </p>
          </div>
          <button
            class="place-order"
            :disabled="!zipValid && !firstName && !lastName && !emailAddress"
          >
            Place order
          </button>
