<script>
import {
  isCartEmpty,
  getTotalMealPrice,
  getTotalBreakfastPrice,
  getSnackPrice,
  getVeganBowlPrice,
} from './../../cartFunctions.js'
import cartMealItems from './../components/cartMealItems.vue'
import cartBreakfastItem from './../views/cartBreakfastItem.vue'
import cartSnackItems from './cartSnackItems.vue'
import cartVeganBowl from './cartVeganBowl.vue'
import {callUrlWithJwt, saveCartContent} from '@/components/apiFunctions'

export default {
  components: {
    cartMealItems,
    cartBreakfastItem,
    cartSnackItems,
    cartVeganBowl,
  },
  data() {
    return {
      cartEmpty: true,
      // 1 => showing the content
      // 2 => showing the order form
      cartState: 1,
      navTitle: 'Shopping cart',
      // deliveryMode 1 => delivery
      // deliveryMode 2 => pickup
      deliveryMode: 1,
      // paymentMode 1 => pay with card
      // paymentMode 2 => paypal
      paymentMode: 1,
      selected: 0,
      firstStore: {},
      secondStore: {},
      currentLocation: this.$store.state.generalInformation.firstStore,
      locations: [
        this.$store.state.generalInformation.firstStore,
        this.$store.state.generalInformation.secondStore,
      ],
      pins: {
        selected:
          'data:image/png;base64,',
        notSelected:
          'data:image/png;base64,',
      },
      // DATA FOR ORDER FLOW
      firstName: '',
      lastName: '',
      emailAddress: '',
      companyName: '',
      deliveryDate: '',
      timeFrame: '',
      // variables for deliversy method address
      deliveryState: '',
      deliveryTown: '',
      deliveryAddress: '',
      deliveryZip: '',
      deliveryCompany: '',
      deliveryPhone: '',
      deliveryNote: '',
      cardLoaded: false,
      loadedCard: null,
      couponShow: false,
      couponCode: '',
      breakfastNames: '',
      breakfastSet: false,
      snacksSet: false,
      snackNames: '',
      deliveryCost: 0,
      zipValid: false,
    }
  },
  created() {
    this.$nuxt.$on('to-payment', () => {
      // switch to payment form
      this.cartState = 2
      this.navTitle = 'Checkout'
      this.saveCartContent()
    })
  },
  mounted() {
    const self = this
    this.cartEmpty = isCartEmpty(this.$store)
    // emit event that we're in the cart view
    $nuxt.$emit('cart-view-entered')

    // todo check if the user is logged in, if yes then fill the info

    if (this.$store.state.logedIn === true) {
      this.loadProfileData()
    }

    this.getBreakfastName()
    this.getSnackName()
    this.saveCartContent()
    // this.$store.state.generalInformation.firstStore,
    // this.$store.state.generalInformation.secondStore,
  },
  beforeDestroy() {
    $nuxt.$emit('cart-view-exited')
    $nuxt.$emit('mobile-confirmation-check')
  },
  methods: {
    async placeOrder() {
      const profileInfo = await callUrlWithJwt(
        this.$axios,
        this.$store,
        this.$store.state.apiConfiguration.baseUrl +
        this.$store.state.apiConfiguration.urlGetProfileInfo,
        'POST',
        {
          firstName: this.firstName,
          lastName: this.lastName,
          emailAddress: this.emailAddress,
          paymentMode: this.paymentMode,
          deliveryMode: this.deliveryMode,
          orderContent: {
            meals: this.$store.state.cart.selectedMeals,
            dishes: this.$store.state.cart.selectedDishes,
            breakfast: this.$store.state.cart.selectedBreakfast,
            snacks: this.$store.state.cart.selectedSnacks,
            bowl: this.$store.state.cart.selectedVeganBowl,
          },
        }
      )
    },
    saveCartContent() {
      saveCartContent(this.$axios, this.$store)
    },
    getTotalPrice() {
      let totalPrice = 0

      if (this.$store.state.cart.selectedBreakfast.length > 0)
        totalPrice += this.getTotalBreakfastPrice()

      if (this.$store.state.cart.selectedSnacks.length > 0)
        totalPrice += this.getTotalSnackPrice()

      if (this.$store.state.cart.selectedVeganBowl.length > 0)
        totalPrice += this.getVeganBowlPriceTotal()

      if (this.deliveryCost != 0)
        totalPrice = this.getTotalMealPrice()

      totalPrice += this.getTotalMealPrice()
      // todo count the delivery price too
      return totalPrice
    },
    goShopping() {
      $nuxt.$emit('toggle-view', 4)
    },
    getTotalSnackPrice() {
      return getSnackPrice(this.$store)
    },
    getVeganBowlPriceTotal() {
      return getVeganBowlPrice(this.$store)
    },
    getBreakfastName() {
      if (this.$store.state.cart.selectedBreakfast.length > 0) {
        this.breakfastSet = true
        this.breakfastNames = ''

        for (
          let i = 0;
          i < this.$store.state.cart.selectedBreakfast.length;
          i++
        ) {
          if (
            typeof this.$store.state.cart.selectedBreakfast[i] !== 'undefined'
          ) {
            if (i != 0) {
              this.breakfastNames +=
                ' & ' + this.$store.state.cart.selectedBreakfast[i].name
            } else {
              this.breakfastNames = this.$store.state.cart.selectedBreakfast[
                i
              ].name
            }
          }
        }
      } else {
        this.breakfastSet = false
      }
    },
    getSnackName() {
      if (this.$store.state.cart.selectedSnacks.length > 0) {
        this.snacksSet = true
        this.snackNames = ''
        for (let i = 0; i < this.$store.state.cart.selectedSnacks.length; i++) {
          if (typeof this.$store.state.cart.selectedSnacks[i] !== 'undefined') {
            if (i != 0) {
              this.snackNames +=
                ' & ' + this.$store.state.cart.selectedSnacks[i].name
            } else {
              this.snackNames = this.$store.state.cart.selectedSnacks[i].name
            }
          }
        }
      } else {
        this.snacksSet = false
      }
    },
    getTotalMealPrice() {
      return getTotalMealPrice(this.$store, true)
    },
    getTotalBreakfastPrice() {
      return getTotalBreakfastPrice(this.$store)
    },
    toggleDelivery() {
      if (this.deliveryMode == 1) this.deliveryMode = 2
      else this.deliveryMode = 1
    },
    togglePayment() {
      if (this.paymentMode == 1) this.paymentMode = 2
      else this.paymentMode = 1
    },
    setStoreMapView(store) {
      this.selected = store
      if (store === 0)
        this.currentLocation = this.$store.state.generalInformation.firstStore
      else if (store === 1)
        this.currentLocation = this.$store.state.generalInformation.secondStore
      this.setMapCenter()
    },
    setMapCenter() {
      this.$refs.gMap.map.setCenter(this.currentLocation)
    },
    async loadProfileData() {
      // if logged in
      const profileInfo = await callUrlWithJwt(
        this.$axios,
        this.$store,
        this.$store.state.apiConfiguration.baseUrl +
          this.$store.state.apiConfiguration.urlGetProfileInfo,
        'POST'
      )

      const deliveryAddress = await callUrlWithJwt(
        this.$axios,
        this.$store,
        this.$store.state.apiConfiguration.baseUrl +
          this.$store.state.apiConfiguration.urlDeliveryAddress,
        'POST'
      )

      const billingAddress = await callUrlWithJwt(
        this.$axios,
        this.$store,
        this.$store.state.apiConfiguration.baseUrl +
          this.$store.state.apiConfiguration.urlBillingAddress,
        'POST'
      )

      const cards = await callUrlWithJwt(
        this.$axios,
        this.$store,
        this.$store.state.apiConfiguration.baseUrl +
          this.$store.state.apiConfiguration.urlGetCards,
        'POST'
      )

      if (typeof cards[0] !== 'undefined') {
        // card found
        this.cardLoaded = true
        this.loadedCard = '•••• •••• ••••' + ' ' + cards[0].four_digits
      }

      this.firstName = profileInfo.first_name
      this.lastName = profileInfo.last_name
      this.emailAddress = profileInfo.email
      this.deliveryState = deliveryAddress.state
      this.deliveryAddress = deliveryAddress.street
      this.deliveryZip = deliveryAddress.zip
      this.deliveryTown = deliveryAddress.town
      this.deliveryCompany = deliveryAddress.company
      this.deliveryPhone = billingAddress.phone_number
    },
    async recalculateZip() {
      const checkPostCode = await this.$axios.$post(
        this.$store.state.apiConfiguration.baseUrl +
          this.$store.state.apiConfiguration.urlCheckDeliveryPostCode,
        {
          post_code: this.deliveryZip,
        }
      )
      if (checkPostCode.status === 'success') {
        let cost = checkPostCode.value.replaceAll('>', '')
        cost = cost.replaceAll('<', '')
        cost = cost.replaceAll('p', '')
        cost = cost.replaceAll('/', '')
        cost = cost.replaceAll('$', '')
        //console.log(cost)
        this.deliveryCost = Number(cost)
        this.zipValid = true
      } else {
        this.deliveryCost = 0
        this.zipValid = false
      }
    },
  },
}
</script>

